mapper.select:
 SELECT *
 FROM product


         AND id = #{id}


         AND name LIKE CONCAT('%', #{name}, '%')


         AND start_time BETWEEN CAST(#{startTime} AS DATETIME) AND CAST(#{endTime} AS DATETIME)


         AND end_time BETWEEN CAST(#{startTime} AS DATETIME) AND CAST(#{endTime} AS DATETIME)


         AND status = #{status}


         AND caretaker = #{caretaker}


         AND proxy_id = #{proxyId}


limit #{currentPage},#{pageSize}

SELECT count(*)
FROM product


        AND id = #{id}


        AND name LIKE CONCAT('%', #{name}, '%')


        AND start_time BETWEEN CAST(#{startTime} AS DATETIME) AND CAST(#{endTime} AS DATETIME)


        AND end_time BETWEEN CAST(#{startTime} AS DATETIME) AND CAST(#{endTime} AS DATETIME)


        AND status = #{status}


        AND caretaker = #{caretaker}


        AND proxy_id = #{proxyId}
